/*Table structure for table `zy_fruit_player` */

DROP TABLE IF EXISTS `zy_fruit_player`;

CREATE TABLE `zy_fruit_player` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nickname` varchar(50) DEFAULT NULL COMMENT '微信昵称',
  `name` varchar(20) DEFAULT NULL COMMENT '收件人姓名',
  `openID` varchar(50) DEFAULT NULL COMMENT '微信openID',
  `sex` varchar(1) DEFAULT NULL COMMENT '性别,值为1时是男性，值为2时是女性，值为0时是未知',
  `head_img` varchar(250) DEFAULT NULL COMMENT '微信头像',
  `local_img` varchar(250) DEFAULT NULL COMMENT '本地头像',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0是，1是',
  `score` int(6) NOT NULL DEFAULT '0',
  `session_id` varchar(120) DEFAULT NULL,
  `phone_os` varchar(512) DEFAULT NULL,
  `addtime` int(12) DEFAULT NULL,
  `lasttime` int(12) NOT NULL DEFAULT '0',
  `ActiveID` int(11) DEFAULT NULL,
  `ChannelID` int(11) DEFAULT NULL,
  `RoomID` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `addtime` (`addtime`),
  KEY `status` (`status`),
  KEY `max_score` (`score`),
  KEY `openID` (`openID`)
) ENGINE=MyISAM AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

